import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaArrowRight, FaFilm, FaThumbsUp, FaStar, FaSearch } from "react-icons/fa";
import '../App.css';

// TMDB API key and URLs
const API_KEY = '3fd2be6f0c70a2a598f084ddfb75487c'; // This is a demo key for testing
const BASE_URL = 'https://api.themoviedb.org/3';
const IMG_URL = 'https://image.tmdb.org/t/p/w500';

const Home = () => {
  const navigate = useNavigate();
  const [trendingMovies, setTrendingMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTrendingMovies = async () => {
      try {
        const response = await fetch(
          `${BASE_URL}/trending/movie/week?api_key=${API_KEY}`
        );
        const data = await response.json();
        setTrendingMovies(data.results.slice(0, 5));
      } catch (error) {
        console.error("Error fetching trending movies:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchTrendingMovies();
  }, []);

  return (
    <div
      className="home-container"
      style={{ backgroundImage: 'url("/homebg.avif")' }}
    >
      <div className="home-card">
        <h2>MovieFlix</h2>
        <p className="home-subtitle">Discover and find your next favorite movie!</p>

        {loading ? (
          <div className="loading">Loading trending movies...</div>
        ) : (
          <div className="trending-section">
            <h3>Trending This Week</h3>
            <div className="trending-movies">
              {trendingMovies.map(movie => (
                <div key={movie.id} className="trending-movie">
                  <img 
                    src={movie.poster_path ? `${IMG_URL}${movie.poster_path}` : "/default-movie.jpg"} 
                    alt={movie.title} 
                    className="trending-poster"
                  />
                  <div className="trending-info">
                    <h4>{movie.title}</h4>
                    <div className="trending-rating">
                      <FaStar className="star-icon" /> {movie.vote_average.toFixed(1)}
                    </div>
                    <p className="trending-year">
                      {movie.release_date ? new Date(movie.release_date).getFullYear() : 'Unknown'} 
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        <div className="feature-section">
          <div className="feature-item">
            <FaFilm className="feature-icon" />
            <h3>Personalized Recommendations</h3>
            <p>Our intelligent algorithm recommends movies based on your preferences</p>
          </div>
          
          <div className="feature-item">
            <FaThumbsUp className="feature-icon" />
            <h3>Like & Save</h3>
            <p>Like movies to get better recommendations tailored to your taste</p>
          </div>
          
          <div className="feature-item">
            <FaSearch className="feature-icon" />
            <h3>Browse by Genre</h3>
            <p>Filter movies by genre to find exactly what you're looking for</p>
          </div>
        </div>

        <div className="cta-section">
          <h3>Ready to discover amazing movies?</h3>
          <p>Login and start exploring our collection of the best films from around the world</p>
          <button 
            className="explore-button" 
            onClick={() => navigate("/movies")}
          >
            Explore Movies <FaArrowRight style={{ marginLeft: '8px' }} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default Home;
