import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaArrowRight, FaFilm, FaThumbsUp, FaStar, FaSearch, FaPlay, FaInfoCircle, FaChevronLeft, FaChevronRight, FaPlayCircle } from "react-icons/fa";
import '../App.css';

// TMDB API key and URLs
const API_KEY = '3fd2be6f0c70a2a598f084ddfb75487c'; // This is a demo key for testing
const BASE_URL = 'https://api.themoviedb.org/3';
const IMG_URL = 'https://image.tmdb.org/t/p/w500';
const BACKDROP_URL = 'https://image.tmdb.org/t/p/original';

const Home = () => {
  const navigate = useNavigate();
  const [trendingMovies, setTrendingMovies] = useState([]);
  const [featuredMovieIndex, setFeaturedMovieIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [activeTrendingPage, setActiveTrendingPage] = useState(0);

  useEffect(() => {
    const fetchTrendingMovies = async () => {
      try {
        const response = await fetch(
          `${BASE_URL}/trending/movie/week?api_key=${API_KEY}`
        );
        const data = await response.json();
        
        // Filter movies with a backdrop
        const moviesWithBackdrop = data.results.filter(m => m.backdrop_path);
        setTrendingMovies(moviesWithBackdrop);
      } catch (error) {
        console.error("Error fetching trending movies:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchTrendingMovies();
  }, []);

  // Auto-carousel effect for hero section
  useEffect(() => {
    // Only start carousel if we have movies
    if (trendingMovies.length > 0) {
      const carouselInterval = setInterval(() => {
        setFeaturedMovieIndex(prevIndex => 
          prevIndex === trendingMovies.length - 1 ? 0 : prevIndex + 1
        );
      }, 6000); // Change movie every 6 seconds
      
      // Clean up interval on component unmount
      return () => clearInterval(carouselInterval);
    }
  }, [trendingMovies]);

  // Navigate to previous movie
  const goToPrevMovie = () => {
    setFeaturedMovieIndex(prevIndex => 
      prevIndex === 0 ? trendingMovies.length - 1 : prevIndex - 1
    );
  };

  // Navigate to next movie
  const goToNextMovie = () => {
    setFeaturedMovieIndex(prevIndex => 
      prevIndex === trendingMovies.length - 1 ? 0 : prevIndex + 1
    );
  };

  // Handle trending carousel
  const showNextTrendingPage = () => {
    const maxPages = Math.ceil(trendingMovies.length / 6) - 1;
    setActiveTrendingPage(prev => (prev === maxPages ? 0 : prev + 1));
  };

  const showPrevTrendingPage = () => {
    const maxPages = Math.ceil(trendingMovies.length / 6) - 1;
    setActiveTrendingPage(prev => (prev === 0 ? maxPages : prev - 1));
  };

  // Current featured movie
  const featuredMovie = trendingMovies[featuredMovieIndex];

  // Calculate trending movies for current page
  const currentTrendingMovies = trendingMovies.slice(
    activeTrendingPage * 6, 
    (activeTrendingPage * 6) + 6
  );

  return (
    <div className="home-page">
      {/* Hero Section with Featured Movie */}
      {featuredMovie && (
        <div 
          className="hero-section" 
          style={{ 
            backgroundImage: `url(${BACKDROP_URL}${featuredMovie.backdrop_path})`,
            transition: 'background-image 1s ease-in-out'
          }}
        >
          {/* Hero Navigation Arrows */}
          <button className="hero-nav-arrow hero-prev" onClick={goToPrevMovie}>
            <FaChevronLeft />
          </button>
          <button className="hero-nav-arrow hero-next" onClick={goToNextMovie}>
            <FaChevronRight />
          </button>
          
          <div className="hero-content">
            <div className="hero-carousel-indicator">
              {trendingMovies.map((_, index) => (
                <span 
                  key={index} 
                  className={`carousel-dot ${index === featuredMovieIndex ? 'active' : ''}`}
                  onClick={() => setFeaturedMovieIndex(index)}
                ></span>
              ))}
            </div>
            <h1 className="hero-title">{featuredMovie.title}</h1>
            <div className="hero-meta">
              <span className="hero-year">
                {featuredMovie.release_date ? new Date(featuredMovie.release_date).getFullYear() : ''}
              </span>
              <span className="hero-rating">
                <FaStar className="hero-star" /> {featuredMovie.vote_average.toFixed(1)}
              </span>
            </div>
            <p className="hero-overview">{featuredMovie.overview}</p>
            <div className="hero-buttons">
              <button 
                className="hero-btn primary-btn"
                onClick={() => navigate('/movies')}
              >
                <FaPlay /> Watch Now
              </button>
              <button 
                className="hero-btn secondary-btn"
                onClick={() => navigate('/movies')}
              >
                <FaInfoCircle /> More Info
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Main Content */}
      <div className="home-main">
        <div className="home-section welcome-section">
          <h2 className="section-title">Welcome to MovieView</h2>
          <p className="section-subtitle">Your ultimate destination for discovering amazing movies</p>
        
          <div className="feature-grid">
            <div className="feature-card">
              <div className="feature-icon-wrapper">
                <FaFilm className="feature-icon" />
              </div>
              <h3>Personalized Recommendations</h3>
              <p>Discover movies tailored to your unique taste through our advanced recommendation system</p>
            </div>
            
            <div className="feature-card">
              <div className="feature-icon-wrapper">
                <FaThumbsUp className="feature-icon" />
              </div>
              <h3>Like & Save</h3>
              <p>Like movies to train our algorithm to understand your preferences better</p>
            </div>
            
            <div className="feature-card">
              <div className="feature-icon-wrapper">
                <FaSearch className="feature-icon" />
              </div>
              <h3>Browse by Genre</h3>
              <p>Filter movies by genre to find exactly what you're in the mood for</p>
            </div>
          </div>
        </div>

        {/* Trending Section */}
        {!loading && trendingMovies.length > 0 && (
          <div className="home-section trending-section">
            <div className="trending-header">
              <h2 className="section-title">Trending This Week</h2>
              <div className="trending-navigation">
                <button className="trending-nav-btn" onClick={showPrevTrendingPage}>
                  <FaChevronLeft />
                </button>
                <div className="trending-indicators">
                  {Array.from({ length: Math.ceil(trendingMovies.length / 6) }).map((_, index) => (
                    <span 
                      key={index} 
                      className={`trending-indicator ${index === activeTrendingPage ? 'active' : ''}`}
                      onClick={() => setActiveTrendingPage(index)}
                    ></span>
                  ))}
                </div>
                <button className="trending-nav-btn" onClick={showNextTrendingPage}>
                  <FaChevronRight />
                </button>
              </div>
            </div>
            <div className="trending-carousel">
              {currentTrendingMovies.map(movie => (
                <div key={movie.id} className="trending-card">
                  <div className="trending-poster-wrapper">
                    <img 
                      src={movie.poster_path ? `${IMG_URL}${movie.poster_path}` : "/default-movie.jpg"} 
                      alt={movie.title} 
                      className="trending-poster"
                    />
                    <div className="trending-overlay">
                      <button className="trending-play-btn" onClick={() => navigate('/movies')}>
                        <FaPlay />
                      </button>
                    </div>
                  </div>
                  <h4 className="trending-title">{movie.title}</h4>
                  <div className="trending-meta">
                    <span className="trending-year">
                      {movie.release_date ? new Date(movie.release_date).getFullYear() : ''}
                    </span>
                    <span className="trending-rating">
                      <FaStar /> {movie.vote_average.toFixed(1)}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
        
        {/* CTA Section */}
        <div className="home-section cta-section">
          <div className="cta-background">
            <div className="cta-overlay"></div>
          </div>
          <div className="cta-content">
            <h2 className="cta-title">Ready to explore?</h2>
            <p className="cta-text">Login to discover and enjoy thousands of movies from our MovieView collection</p>
            <div className="cta-buttons">
              <button 
                className="cta-button primary-cta" 
                onClick={() => navigate("/movies")}
              >
                Explore Movies <FaArrowRight />
              </button>
              <button 
                className="cta-button secondary-cta" 
                onClick={() => navigate("/movies")}
              >
                <FaPlayCircle /> Top Picks
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
