import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { FaArrowLeft, FaArrowRight, FaStar, FaThumbsUp } from "react-icons/fa";
import MoviePoster from './MoviePoster';
import '../App.css';

const MovieList = () => {
  const navigate = useNavigate();
  
  // Sample movie data with local image URLs
  const [movies] = useState([
    { id: 1, title: 'The Shawshank Redemption', year: 1994, rating: 9.3, genre: 'Drama', poster: '/movie1.jpg' },
    { id: 2, title: 'The Godfather', year: 1972, rating: 9.2, genre: 'Crime, Drama', poster: '/movie2.jpg' },
    { id: 3, title: 'The Dark Knight', year: 2008, rating: 9.0, genre: 'Action, Crime, Drama', poster: '/movie3.jpg' },
    { id: 4, title: 'The Godfather Part II', year: 1974, rating: 9.0, genre: 'Crime, Drama', poster: '/movie4.jpg' },
    { id: 5, title: 'Pulp Fiction', year: 1994, rating: 8.9, genre: 'Crime, Drama', poster: '/movie5.jpg' },
    { id: 6, title: 'Inception', year: 2010, rating: 8.8, genre: 'Action, Adventure, Sci-Fi', poster: '/movie6.jpg' },
    { id: 7, title: 'Fight Club', year: 1999, rating: 8.8, genre: 'Drama', poster: '/movie7.jpg' },
    { id: 8, title: 'The Matrix', year: 1999, rating: 8.7, genre: 'Action, Sci-Fi', poster: '/movie8.jpg' },
    { id: 9, title: 'Goodfellas', year: 1990, rating: 8.7, genre: 'Crime, Drama', poster: '/movie9.jpg' },
    { id: 10, title: 'Interstellar', year: 2014, rating: 8.6, genre: 'Adventure, Drama, Sci-Fi', poster: '/movie10.jpg' },
  ]);

  const [userPreferences, setUserPreferences] = useState({
    favoriteGenres: [],
    likedMovies: []
  });

  const [recommendedMovies, setRecommendedMovies] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState('All');

  // Extract all unique genres
  const allGenres = ['All', ...new Set(movies.flatMap(movie => movie.genre.split(', ')))];

  // Filter movies by selected genre
  const filteredMovies = selectedGenre === 'All' 
    ? movies 
    : movies.filter(movie => movie.genre.includes(selectedGenre));

  // Like/favorite a movie
  const likeMovie = (movieId) => {
    const movie = movies.find(m => m.id === movieId);
    if (movie) {
      // Add movie to liked movies
      const likedMovies = [...userPreferences.likedMovies, movieId];
      
      // Update favorite genres based on this movie
      const genres = movie.genre.split(', ');
      const favoriteGenres = [...new Set([...userPreferences.favoriteGenres, ...genres])];
      
      setUserPreferences({
        favoriteGenres,
        likedMovies
      });
    }
  };

  // Generate recommendations based on user preferences
  useEffect(() => {
    if (userPreferences.favoriteGenres.length > 0) {
      // Get recommendations based on favorite genres and not already liked
      const recommendations = movies
        .filter(movie => !userPreferences.likedMovies.includes(movie.id))
        .filter(movie => {
          const movieGenres = movie.genre.split(', ');
          return movieGenres.some(genre => userPreferences.favoriteGenres.includes(genre));
        })
        .sort((a, b) => b.rating - a.rating); // Sort by rating
      
      setRecommendedMovies(recommendations);
    }
  }, [userPreferences, movies]);

  // Default movie image when actual image is not available
  const handleImageError = (e) => {
    e.target.src = "/default-movie.jpg";
    e.target.onerror = null; // Prevent infinite loop
  };

  return (
    <div className="container">
      <div className="back-arrow" onClick={() => navigate("/home")}>
        <FaArrowLeft />
      </div>
      
      <h2>Movies</h2>
      
      {/* Genre Filter */}
      <div className="genre-filter">
        <label>Filter by Genre: </label>
        <select 
          value={selectedGenre} 
          onChange={(e) => setSelectedGenre(e.target.value)}
          style={{ padding: '8px', marginLeft: '10px', borderRadius: '4px' }}
        >
          {allGenres.map(genre => (
            <option key={genre} value={genre}>{genre}</option>
          ))}
        </select>
      </div>
      
      {/* Recommended Movies Section */}
      {recommendedMovies.length > 0 && (
        <div className="recommended-section">
          <h3>Recommended For You</h3>
          <div className="movie-grid">
            {recommendedMovies.slice(0, 3).map(movie => (
              <div key={`rec-${movie.id}`} className="movie-card">
                <MoviePoster posterUrl={movie.poster} title={movie.title} />
                <h4>{movie.title} ({movie.year})</h4>
                <p>Rating: {movie.rating}/10</p>
                <p>Genre: {movie.genre}</p>
                <button 
                  onClick={() => likeMovie(movie.id)}
                  className="like-button"
                  disabled={userPreferences.likedMovies.includes(movie.id)}
                >
                  <FaThumbsUp /> {userPreferences.likedMovies.includes(movie.id) ? 'Liked' : 'Like'}
                </button>
              </div>
            ))}
          </div>
        </div>
      )}
      
      {/* All Movies Section */}
      <h3>All Movies {selectedGenre !== 'All' ? `- ${selectedGenre}` : ''}</h3>
      <div className="movie-grid">
        {filteredMovies.map(movie => (
          <div key={movie.id} className="movie-card">
            <MoviePoster posterUrl={movie.poster} title={movie.title} />
            <h4>{movie.title} ({movie.year})</h4>
            <div className="rating">
              <FaStar className="star-icon" /> {movie.rating}/10
            </div>
            <p>Genre: {movie.genre}</p>
            <button 
              onClick={() => likeMovie(movie.id)}
              className={`like-button ${userPreferences.likedMovies.includes(movie.id) ? 'liked' : ''}`}
              disabled={userPreferences.likedMovies.includes(movie.id)}
            >
              <FaThumbsUp /> {userPreferences.likedMovies.includes(movie.id) ? 'Liked' : 'Like'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MovieList;
