import React from "react";
import { useNavigate } from "react-router-dom";
import { FaArrowLeft, FaArrowRight, FaFilm, FaThumbsUp, FaStar } from "react-icons/fa";
import '../App.css';

const Home = () => {
  const navigate = useNavigate();

  const featuredMovies = [
    { title: "The Shawshank Redemption", rating: 9.3 },
    { title: "The Godfather", rating: 9.2 },
    { title: "The Dark Knight", rating: 9.0 }
  ];

  return (
    <div
      className="home-container"
      style={{ backgroundImage: 'url("/homebg.avif")' }}
    >
      <div className="home-card">
        <div className="back-arrow" onClick={() => navigate("/movies")}>
          <FaArrowLeft />
        </div>
        <div className="forward-arrow" onClick={() => navigate("/login")}>
          <FaArrowRight />
        </div>

        <h2>Welcome to MovieFlix</h2>
        <p>Discover and find your next favorite movie!</p>

        <div className="feature-section">
          <div className="feature-item">
            <FaFilm className="feature-icon" />
            <h3>Extensive Collection</h3>
            <p>Browse through our curated collection of top-rated movies</p>
          </div>
          
          <div className="feature-item">
            <FaThumbsUp className="feature-icon" />
            <h3>Personalized Recommendations</h3>
            <p>Like movies to get personalized recommendations based on your preferences</p>
          </div>
          
          <div className="feature-item">
            <FaStar className="feature-icon" />
            <h3>Top Rated Films</h3>
            <p>Discover critically acclaimed movies with high ratings</p>
          </div>
        </div>

        <div className="featured-movies">
          <h3>Featured Movies</h3>
          <div className="featured-list">
            {featuredMovies.map((movie, index) => (
              <div key={index} className="featured-movie">
                <span className="movie-title">{movie.title}</span>
                <span className="movie-rating">
                  <FaStar className="star-icon" /> {movie.rating}
                </span>
              </div>
            ))}
          </div>
        </div>

        <button 
          className="explore-button" 
          onClick={() => navigate("/movies")}
        >
          Explore Movies
        </button>
      </div>
    </div>
  );
};

export default Home;
